<template>
    <div>
      <ul>
        <!--itemsの件数文回している-->
        <li v-for="item in items">
          <img class="friendimg" :src=item.img>
          <div>
          {{ item.name }}
          <router-link to="/game">
            <button type="button" class="btn btn-primary btn-sm">攻略</button>
          </router-link>
          </div>
          <div>
            {{item.jusyo}}
          </div>
        </li>
      </ul>
      <!--itemsが増えた際にリストに追加させているかのテスト用 何をのせるのか不明のためとりあえずの項目名-->
      名前: <input v-model="addName"><br />
      住所: <input v-model="addJusyo"><br />
      <button v-on:click="add">add</button><br />
    </div>
</template>

<script>
    export default {
      name: "FriendList",
      data: function () {
        return {
          //サンプルデータです。画像はネットから画像アドレスで入れてます。
          items: [
            {img:'data:image/jpeg;base64,',name: '加藤純一', jusyo: 'レジディア西新宿Ⅱ'},
            {img:'data:image/jpeg;base64,',name: 'under the tree', jusyo: '博多駅南'}
          ],
        };
      },
      methods: {
        add: function (event) {
          this.items.push({ name: this.addName, jusyo: this.addJusyo })
          this.addName = '';
          this.addJusyo = '';
        }
      }
    }
</script>

<style scoped>
  ul{
    list-style: none;
  }
  /*上下の間隔と背景に影をつけている。*/
  li{
    background: #f6f6f6;
    box-shadow: 0px 3px 5px rgba(0, 0, 0, 0.3);
    padding: 10px 20px;
    margin-bottom: 10px;
  }
  .friendimg{
    float: left;
    /*リストでの背景に入らないため画像サイズを調整している。項目が増えると変更する必要あり*/
    max-width: 55px; /* 最大幅 */
    width: 15%;
  }

</style>
